mapper.select:
SELECT
FROM critic LEFT JOIN critic_top ON critic.id = critic_top.critic_id LEFT JOIN critic_img ON critic.image_id = critic_img.id INNER JOIN critic_list_item ON critic_list_item.critic_id = critic.id WHERE critic_list_item.list_id = #{group_id} AND critic.deleted_ind = 0 ORDER BY critic.name
LIMIT #{offset},#{limit}
LIMIT #{limit}

SELECT
FROM critic LEFT JOIN critic_top ON critic.id = critic_top.critic_id LEFT JOIN critic_img ON critic.image_id = critic_img.id WHERE critic.deleted_ind = 0
AND name LIKE #{initial}
AND (critic.status = 'CURRENT' OR critic.status = 'ADMIN_PROTECTED_CURRENT')
AND (critic.status = 'LEGACY' OR critic.status = 'ADMIN_PROTECTED_LEGACY')
ORDER BY critic.name
LIMIT #{offset},#{limit}
LIMIT #{limit}
