mapper.select:
SELECT multiuse_img_reference.media_type AS mediaType, multiuse_img.id AS id, multiuse_img.format AS format, multiuse_img.img_width AS originalWidth, multiuse_img.img_height AS originalHeight, multiuse_img.aspect_ratio AS aspectRatio FROM multiuse_img_reference LEFT JOIN multiuse_img ON multiuse_img.id = multiuse_img_reference.multiuse_image_id WHERE multiuse_img_reference.media_id=#{movie_id}
LIMIT #{offset},#{limit}
LIMIT #{limit}
