mapper.select:
SELECT
FROM movie_cast LEFT OUTER JOIN movie_person ON movie_cast.person_id = movie_person.id AND (movie_person.deleted_ind IS NULL OR movie_person.deleted_ind != 1) LEFT OUTER JOIN movie_character ON movie_cast.id = movie_character.cast_id AND (movie_character.deleted_ind IS NULL OR movie_character.deleted_ind != 1)
LEFT OUTER JOIN rtactor_img ON preferred_img_type='RTA' AND preferred_img_id=rtactor_img.id LEFT OUTER JOIN multiuse_img ON preferred_img_type='MUI' AND preferred_img_id=multiuse_img.id LEFT OUTER JOIN photo_img ON preferred_img_type='PHI' AND preferred_img_id=photo_img.id
WHERE (movie_cast.deleted_ind IS NULL OR movie_cast.deleted_ind != 1) AND movie_cast.movie_id = #{movie_id}
AND movie_cast.role = #{role}
ORDER BY FIELD(movie_cast.role, 'DIR','PRO','SCR','EPR','ACT'), /* Return actors last */ movie_cast.sortkey
LIMIT #{offset},#{limit}
LIMIT #{limit}
