mapper.select:
SELECT review.*, critic.name critic_name, publication.name publication_name, IF(critic_top.critic_id,1,0) is_top_critic
FROM review LEFT JOIN critic ON review.critic_id=critic.id LEFT JOIN publication ON review.publication_id=publication.id LEFT JOIN critic_top ON (review.critic_id=critic_top.critic_id AND critic_top.country='US') WHERE review.movie_id = #{movie_id} AND review.status = 'A' ORDER BY is_top_critic DESC, creation_date DESC
LIMIT #{limit}
